import React, { useState, useEffect } from 'react';
import '../App.css';

function Groceries() {
  const [inventory, setInventory] = useState([]);
  const [shoppingList, setShoppingList] = useState([]);
  const [newItem, setNewItem] = useState({ name: '', quantity: '' });
  const [activeTab, setActiveTab] = useState('inventory');

  useEffect(() => {
    // Load data from local storage on initial load
    const storedInventory = JSON.parse(localStorage.getItem('inventory')) || [];
    const storedShoppingList = JSON.parse(localStorage.getItem('shoppingList')) || [];
    setInventory(storedInventory);
    setShoppingList(storedShoppingList);
  }, []);

  useEffect(() => {
    // Save data to local storage whenever they change
    localStorage.setItem('inventory', JSON.stringify(inventory));
    localStorage.setItem('shoppingList', JSON.stringify(shoppingList));
  }, [inventory, shoppingList]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewItem({ ...newItem, [name]: value });
  };

  const handleAddItem = () => {
    if (newItem.name && newItem.quantity) {
      if (activeTab === 'inventory') {
        setInventory([...inventory, { ...newItem, id: Date.now() }]);
      } else {
        setShoppingList([...shoppingList, { ...newItem, id: Date.now(), purchased: false }]);
      }
      setNewItem({ name: '', quantity: '' });
    }
  };

  const handleDeleteItem = (id) => {
    if (activeTab === 'inventory') {
      setInventory(inventory.filter(item => item.id !== id));
    } else {
      setShoppingList(shoppingList.filter(item => item.id !== id));
    }
  };

  const handleTogglePurchased = (id) => {
    setShoppingList(shoppingList.map(item =>
      item.id === id ? { ...item, purchased: !item.purchased } : item
    ));
  };

  const handleMoveToInventory = (id) => {
    const itemToMove = shoppingList.find(item => item.id === id);
    if (itemToMove) {
      setShoppingList(shoppingList.filter(item => item.id !== id));
      setInventory([...inventory, { ...itemToMove, purchased: false }]);
    }
  };

  return (
    <div className="groceries-container">
      <h2>Grocery Manager</h2>
      <div className="tab-selector">
        <button onClick={() => setActiveTab('inventory')} className={activeTab === 'inventory' ? 'active' : ''}>Inventory</button>
        <button onClick={() => setActiveTab('shoppingList')} className={activeTab === 'shoppingList' ? 'active' : ''}>Shopping List</button>
      </div>
      <div className="add-item">
        <input
          type="text"
          name="name"
          value={newItem.name}
          onChange={handleInputChange}
          placeholder="Item Name"
        />
        <input
          type="text"
          name="quantity"
          value={newItem.quantity}
          onChange={handleInputChange}
          placeholder="Quantity"
        />
        <button onClick={handleAddItem}>Add Item</button>
      </div>
      <div className="items-list">
        {activeTab === 'inventory' ? (
          <>
            <h3>Inventory</h3>
            {inventory.length > 0 ? (
              <table>
                <thead>
                  <tr>
                    <th>Item Name</th>
                    <th>Quantity</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {inventory.map(item => (
                    <tr key={item.id}>
                      <td>{item.name}</td>
                      <td>{item.quantity}</td>
                      <td>
                        <button className="delete-btn" onClick={() => handleDeleteItem(item.id)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p>No items in inventory.</p>
            )}
          </>
        ) : (
          <>
            <h3>Shopping List</h3>
            {shoppingList.length > 0 ? (
              <table>
                <thead>
                  <tr>
                    <th>Item Name</th>
                    <th>Quantity</th>
                    <th>Purchased</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {shoppingList.map(item => (
                    <tr key={item.id}>
                      <td>{item.name}</td>
                      <td>{item.quantity}</td>
                      <td>
                        <input
                          type="checkbox"
                          checked={item.purchased}
                          onChange={() => handleTogglePurchased(item.id)}
                        />
                      </td>
                      <td>
                        <button className="delete-btn" onClick={() => handleDeleteItem(item.id)}>Delete</button>
                        {item.purchased && (
                          <button onClick={() => handleMoveToInventory(item.id)}>Move to Inventory</button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p>No items in shopping list.</p>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default Groceries;
