import React, { useState, useEffect } from 'react';
import '../App.css';

function WaterTracker() {
  const [waterLogs, setWaterLogs] = useState([]);
  const [newLog, setNewLog] = useState({ glasses: '', date: '' });
  const [dailyTotal, setDailyTotal] = useState(0);
  const [goal, setGoal] = useState(8); // Default goal of 8 glasses per day

  useEffect(() => {
    // Load water logs from local storage on initial load
    const storedLogs = JSON.parse(localStorage.getItem('waterLogs')) || [];
    const storedGoal = JSON.parse(localStorage.getItem('waterGoal')) || 8;
    setWaterLogs(storedLogs);
    setGoal(storedGoal);
    calculateDailyTotal(storedLogs);
  }, []);

  useEffect(() => {
    // Save water logs and goal to local storage whenever they change
    localStorage.setItem('waterLogs', JSON.stringify(waterLogs));
    localStorage.setItem('waterGoal', JSON.stringify(goal));
    calculateDailyTotal(waterLogs);
  }, [waterLogs, goal]);

  const calculateDailyTotal = (logs) => {
    const today = new Date().toLocaleDateString();
    const sum = logs
      .filter(log => log.date === today)
      .reduce((acc, log) => acc + parseInt(log.glasses || 0), 0);
    setDailyTotal(sum);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewLog({ ...newLog, [name]: value });
  };

  const handleGoalChange = (e) => {
    setGoal(parseInt(e.target.value) || 0);
  };

  const handleAddLog = () => {
    if (newLog.glasses && newLog.date) {
      setWaterLogs([...waterLogs, { ...newLog, id: Date.now() }]);
      setNewLog({ glasses: '', date: '' });
    }
  };

  const handleDeleteLog = (id) => {
    setWaterLogs(waterLogs.filter(log => log.id !== id));
  };

  const handleQuickAdd = () => {
    const today = new Date().toISOString().split('T')[0]; // Format: YYYY-MM-DD
    setWaterLogs([...waterLogs, { glasses: '1', date: today, id: Date.now() }]);
  };

  return (
    <div className="water-container">
      <h2>Water Intake Tracker</h2>
      <div className="goal-setting">
        <label htmlFor="goal">Daily Water Goal (glasses): </label>
        <input
          type="number"
          id="goal"
          value={goal}
          onChange={handleGoalChange}
          placeholder="Set Goal"
          step="1"
        />
      </div>
      <div className="progress-bar">
        <div
          className="progress"
          style={{ width: `${Math.min((dailyTotal / goal) * 100, 100)}%` }}
        >
          {dailyTotal} / {goal} glasses
        </div>
      </div>
      <div className="quick-add">
        <button onClick={handleQuickAdd}>Add 1 Glass (Today)</button>
      </div>
      <div className="add-log">
        <input
          type="number"
          name="glasses"
          value={newLog.glasses}
          onChange={handleInputChange}
          placeholder="Glasses"
          step="1"
        />
        <input
          type="date"
          name="date"
          value={newLog.date}
          onChange={handleInputChange}
        />
        <button onClick={handleAddLog}>Add Log</button>
      </div>
      <div className="logs-list">
        {waterLogs.length > 0 ? (
          <table>
            <thead>
              <tr>
                <th>Date</th>
                <th>Glasses</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {waterLogs.map(log => (
                <tr key={log.id}>
                  <td>{log.date}</td>
                  <td>{log.glasses}</td>
                  <td>
                    <button className="delete-btn" onClick={() => handleDeleteLog(log.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No water intake logged yet.</p>
        )}
      </div>
    </div>
  );
}

export default WaterTracker;
