  const response = await apiClient.post('/protein-logs', proteinLog);
  return response.data;
};

export const updateProteinLog = async (id, proteinLog) => {
  const response = await apiClient.put(`/protein-logs/${id}`, proteinLog);
  return response.data;
};

export const deleteProteinLog = async (id) => {
  await apiClient.delete(`/protein-logs/${id}`);
};

